import Display from "./Display";
import Paneldebotones from "./Paneldebotones";
import {Component} from "react";
import operaciones from "../logic/operaciones";

class App extends Component {

  state = {

    total: null,
    siguiente: null,
    operador: null
  }
  handleClick = nombreboton => this.state(operaciones(this.state,nombreboton))

  render(){
  return (
    <div>
       <Display value={this.state.siguiente || this.state.total } /> 
       <Paneldebotones clickHandler={this.clickHandler} />
    </div>
  );
  }
}

export default App;
